import "./App.css";
import { useState } from "react";
import axios from "axios";

const App = () => {
  const [inputMsg, setinputMsg] = useState("");
  const [chatMsg, setChatMsg] = useState([
    {
      user: "bot",
      message: "Comment puis-je vous aider ?",
    },
  ]);
  const [loading, setLoading] = useState(false);
  const [botMessage, setBotMessage] = useState("");
  const [userMessage, setUserMessage] = useState("");
  const url = "http://localhost:9000/api";
  console.log(url);

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("url", url);
    console.log("input", inputMsg);
    try {
      const { data } = await axios.post(url, { message: inputMsg });
      console.log("data", data.message);
      setUserMessage(inputMsg);
      setBotMessage(data.message);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="flex items-center justify-center  h-screen">
      <div className="border-2  rounded-md border-cyan-800 p-4 w-[75%] h-[75%] flex flex-col ">
        <div className="border-4 rounded-md border-red-600 justify-between p-4 h-[90%]">
          <div className="chat chat-end">
            {userMessage && (
              <div className="chat-bubble chat-bubble-info">{userMessage}</div>
            )}
          </div>
          <div className="chat chat-start">
            {botMessage && (
              <div className="chat-bubble chat-bubble-accent">{botMessage}</div>
            )}
          </div>
        </div>
        <form onSubmit={handleSubmit} className=" space-x-4 p-4">
          <input
            type="text"
            className="w-[80%]"
            placeholder="Enter your message ..."
            value={inputMsg}
            onChange={(e) => setinputMsg(e.target.value)}
          />
          <button
            type="submit"
            className="p-2  bg-blue-700 rounded-md w-[15%] text-white"
          >
            Send
          </button>
        </form>
      </div>
    </div>
  );
};

export default App;
